import React from 'react';
import '../styles/PersonTable.css';

const PersonTable = ({ persons, onSort, sortConfig }) => {
  const getSortIcon = (key) => {
    if (sortConfig.key !== key) return 'â†•ï¸';
    return sortConfig.direction === 'asc' ? 'â¬†ï¸' : 'â¬‡ï¸';
  };

  const getGenreIcon = (genre) => {
    return genre === 'masculin' ? 'ðŸ‘¨' : genre === 'feminin' ? 'ðŸ‘©' : '';
  };

  const getGenreLabel = (genre) => {
    return genre === 'masculin' ? 'Homme' : genre === 'feminin' ? 'Femme' : '';
  };

  const getSituationMatrimonialeIcon = (situation) => {
    const icons = {
      'celibataire': 'ðŸ’›',
      'marie': 'ðŸ’',
      'divorce': 'ðŸ’”',
      'veuf': 'âš«'
    };
    return icons[situation] || '';
  };

  const getSituationMatrimonialeLabel = (situation) => {
    const situations = {
      'celibataire': 'CÃ©libataire',
      'marie': 'MariÃ©(e)',
      'divorce': 'DivorcÃ©(e)',
      'veuf': 'Veuf/Veuve'
    };
    return situations[situation] || '';
  };

  const getStatusIcon = (travail) => {
    return travail ? 'ðŸ’¼' : 'ðŸ‘¤';
  };

  return (
    <div className="person-table-container">
      <div className="table-responsive">
        <table className="person-table">
          <thead>
            <tr>
              <th onClick={() => onSort('prenom')} className="sortable">
                PrÃ©nom {getSortIcon('prenom')}
              </th>
              <th onClick={() => onSort('nom')} className="sortable">
                Nom {getSortIcon('nom')}
              </th>
              <th>Genre</th>
              <th>Situation</th>
              <th>TÃ©lÃ©phone</th>
              <th>Localisation</th>
              <th onClick={() => onSort('profession')} className="sortable">
                Profession/Formation {getSortIcon('profession')}
              </th>
              <th>Entreprise</th>
              <th>Daara</th>
            </tr>
          </thead>
          <tbody>
            {persons.map(person => (
              <tr 
                key={person.id} 
                className={`table-row ${person.travail ? 'travail-row' : 'non-travail-row'}`}
              >
                <td className="person-name" data-label="PrÃ©nom">
                  <strong>{person.prenom}</strong>
                </td>
                <td className="person-name" data-label="Nom">
                  <strong>{person.nom}</strong>
                </td>
                <td className="person-genre" data-label="Genre">
                  <div className="genre-cell">
                    <span className="genre-icon">{getGenreIcon(person.genre)}</span>
                    <span className="genre-label">{getGenreLabel(person.genre)}</span>
                  </div>
                </td>
                <td className="person-situation" data-label="Situation matrimoniale">
                  <div className="situation-cell">
                    <span className="situation-icon">{getSituationMatrimonialeIcon(person.situationMatrimoniale)}</span>
                    <span className="situation-label">{getSituationMatrimonialeLabel(person.situationMatrimoniale)}</span>
                  </div>
                </td>
                <td className="person-phone" data-label="TÃ©lÃ©phone">
                  {person.telephone}
                </td>
                <td className="person-location" data-label="Localisation">
                  <div className="location-main">{person.quartier}</div>
                  <div className="location-detail">{person.region}, {person.pays}</div>
                </td>
                <td className="person-profession" data-label={person.travail ? "Profession" : "Formation"}>
                  {person.travail ? person.profession : person.formation}
                </td>
                <td className="person-entreprise" data-label="Entreprise">
                  {person.entreprise || '-'}
                </td>
                <td className="person-daara" data-label="Daara">
                  {person.daara}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default PersonTable;
