import React from 'react';
import '../styles/Header.css';

const Header = ({ currentPage, setCurrentPage }) => {
  return (
    <header className="header">
      <div className="container">
        <div className="header-content">
          <div className="logo-section">
            <div className="logo-container">
              {/* Essayez ces diff√©rentes m√©thodes */}
              <img 
                src="/logo.png" 
                alt="B√©gu√© Baara" 
                className="logo-image"
                onError={(e) => {
                  e.target.style.display = 'none';
                  // Fallback vers l'emoji si le logo ne charge pas
                  const fallback = document.createElement('div');
                  fallback.className = 'logo-icon';
                  fallback.textContent = 'üïå';
                  e.target.parentNode.insertBefore(fallback, e.target);
                }}
              />
              <div className="logo-text">
                <h1 className="logo">B√©gu√© Baara</h1>
                <span className="tagline">R√©seau Allah Don</span>
              </div>
            </div>
          </div>
          
          <nav className="nav">
            <button 
              className={`nav-btn ${currentPage === 'home' ? 'active' : ''}`}
              onClick={() => setCurrentPage('home')}
            >
              üè† Accueil
            </button>
            <button 
              className={`nav-btn ${currentPage === 'register' ? 'active' : ''}`}
              onClick={() => setCurrentPage('register')}
            >
              ‚úçÔ∏è S'inscrire
            </button>
            <button 
              className={`nav-btn ${currentPage === 'search' ? 'active' : ''}`}
              onClick={() => setCurrentPage('search')}
            >
              üîç Rechercher
            </button>
          </nav>
        </div>
      </div>
    </header>
  );
};

export default Header;
