import React, { useRef, useEffect, useState } from 'react';
import '../styles/Header.css';

const Header = ({ currentPage, setCurrentPage }) => {
  const navRef = useRef(null);
  const [enteteWidth, setEnteteWidth] = useState('80%');

  useEffect(() => {
    const updateEnteteWidth = () => {
      if (navRef.current) {
        const navWidth = navRef.current.offsetWidth;
        const availableWidth = window.innerWidth - 40; // 20px padding de chaque c√¥t√©
        const calculatedWidth = `calc(100% - ${navWidth + 20}px)`;
        setEnteteWidth(calculatedWidth);
      }
    };

    updateEnteteWidth();
    window.addEventListener('resize', updateEnteteWidth);
    
    return () => window.removeEventListener('resize', updateEnteteWidth);
  }, []);

  return (
    <header className="header">
      <div className="container">
        <div className="header-content">
          <div 
            className="entete-section" 
            style={{ maxWidth: enteteWidth }}
          >
            <div className="entete-container">
              <img 
                src="/logo.png" 
                alt="R√©seau Allah Don" 
                className="entete-image"
                onError={(e) => {
                  e.target.style.display = 'none';
                  const fallback = document.createElement('div');
                  fallback.className = 'entete-icon';
                  fallback.textContent = 'üïå';
                  e.target.parentNode.insertBefore(fallback, e.target);
                }}
              />
              <div className="entete-text">
                <span className="tagline">R√©seau Allah Don</span>
              </div>
            </div>
          </div>
          
          <nav className="nav" ref={navRef}>
            <button 
              className={`nav-btn ${currentPage === 'home' ? 'active' : ''}`}
              onClick={() => setCurrentPage('home')}
            >
              üè† Accueil
            </button>
            <button 
              className={`nav-btn ${currentPage === 'register' ? 'active' : ''}`}
              onClick={() => setCurrentPage('register')}
            >
              ‚úçÔ∏è S'inscrire
            </button>
            <button 
              className={`nav-btn ${currentPage === 'search' ? 'active' : ''}`}
              onClick={() => setCurrentPage('search')}
            >
              üîç Rechercher
            </button>
          </nav>
        </div>
      </div>
    </header>
  );
};

export default Header;
