import React, { useState } from 'react';
import '../styles/PersonForm.css';

const PersonForm = ({ onAddPerson, selectedChoice, onBack, persons }) => {
  const [formData, setFormData] = useState({
    nom: '',
    prenom: '',
    telephone: '',
    quartier: '',
    region: '',
    pays: 'Mali',
    genre: '',
    situationMatrimoniale: '',
    profession: '',
    entreprise: '',
    formation: '',
    daara: ''
  });

  const [showConfirmation, setShowConfirmation] = useState(false);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [errors, setErrors] = useState({});

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    
    // Effacer l'erreur quand l'utilisateur modifie le champ
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  // Fonction pour vÃ©rifier les doublons complets
  const checkForCompleteDuplicates = () => {
    const newErrors = {};
    
    if (selectedChoice === 'travail') {
      // VÃ©rification pour les travailleurs: prenom, nom, profession, entreprise, telephone
      const duplicateTravailleur = persons.find(person => 
        person.travail === true &&
        person.prenom?.toLowerCase() === formData.prenom.toLowerCase() &&
        person.nom?.toLowerCase() === formData.nom.toLowerCase() &&
        person.profession?.toLowerCase() === formData.profession.toLowerCase() &&
        person.entreprise?.toLowerCase() === formData.entreprise.toLowerCase() &&
        person.telephone === formData.telephone
      );
      
      if (duplicateTravailleur) {
        newErrors.profession = `ğŸš« Doublon dÃ©tectÃ© ! \n\n${formData.prenom} ${formData.nom} est dÃ©jÃ  enregistrÃ© avec :\nâ€¢ Profession: ${formData.profession}\nâ€¢ Entreprise: ${formData.entreprise}\nâ€¢ TÃ©lÃ©phone: ${formData.telephone}\n\nVeuillez modifier ces informations.`;
      }
    }
    
    if (selectedChoice === 'non-travail') {
      // VÃ©rification pour les non-travailleurs: prenom, nom, formation, telephone
      const duplicateNonTravailleur = persons.find(person => 
        person.travail === false &&
        person.prenom?.toLowerCase() === formData.prenom.toLowerCase() &&
        person.nom?.toLowerCase() === formData.nom.toLowerCase() &&
        person.formation?.toLowerCase() === formData.formation.toLowerCase() &&
        person.telephone === formData.telephone
      );
      
      if (duplicateNonTravailleur) {
        newErrors.formation = `ğŸš« Doublon dÃ©tectÃ© ! \n\n${formData.prenom} ${formData.nom} est dÃ©jÃ  enregistrÃ© avec :\nâ€¢ MÃ©tier de formation: ${formData.formation}\nâ€¢ TÃ©lÃ©phone: ${formData.telephone}\n\nVeuillez modifier ces informations.`;
      }
    }
    
    return newErrors;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    
    // VÃ©rifier que tous les champs requis sont remplis
    if (selectedChoice === 'travail' && (!formData.profession || !formData.entreprise)) {
      setErrors({ profession: 'Veuillez remplir tous les champs requis pour les travailleurs' });
      return;
    }
    
    if (selectedChoice === 'non-travail' && !formData.formation) {
      setErrors({ formation: 'Veuillez remplir le mÃ©tier de formation' });
      return;
    }
    
    // VÃ©rifier les doublons complets avant d'afficher la confirmation
    const duplicateErrors = checkForCompleteDuplicates();
    
    if (Object.keys(duplicateErrors).length > 0) {
      setErrors(duplicateErrors);
      return;
    }
    
    setErrors({});
    setShowConfirmation(true);
  };

  const confirmSubmission = async () => {
    // VÃ©rifier une derniÃ¨re fois les doublons avant l'envoi
    const duplicateErrors = checkForCompleteDuplicates();
    
    if (Object.keys(duplicateErrors).length > 0) {
      setErrors(duplicateErrors);
      setShowConfirmation(false);
      return;
    }
    
    setIsSubmitting(true);
    
    const personToAdd = {
      nom: formData.nom,
      prenom: formData.prenom,
      telephone: formData.telephone,
      quartier: formData.quartier,
      region: formData.region,
      pays: formData.pays,
      genre: formData.genre,
      situationMatrimoniale: formData.situationMatrimoniale,
      profession: formData.profession,
      entreprise: formData.entreprise,
      formation: formData.formation,
      daara: formData.daara,
      travail: selectedChoice === 'travail'
    };

    const result = await onAddPerson(personToAdd);
    
    if (result.success) {
      setFormData({
        nom: '', prenom: '', telephone: '', quartier: '', region: '',
        pays: 'Mali', genre: '', situationMatrimoniale: '', profession: '', entreprise: '', 
        formation: '', daara: ''
      });
      setShowConfirmation(false);
      setErrors({});
      alert('âœ… Inscription rÃ©ussie ! Votre profil est maintenant visible par tous.');
      onBack();
    } else {
      alert('âŒ Erreur: ' + result.error);
    }
    
    setIsSubmitting(false);
  };

  const cancelSubmission = () => {
    setShowConfirmation(false);
  };

  const getFieldDisplayValue = (value) => {
    return value || 'Non renseignÃ©';
  };

  const getGenreLabel = (value) => {
    const genres = {
      'masculin': 'Masculin',
      'feminin': 'FÃ©minin',
      '': 'Non renseignÃ©'
    };
    return genres[value] || value;
  };

  const getSituationMatrimonialeLabel = (value) => {
    const situations = {
      'celibataire': 'CÃ©libataire',
      'marie': 'MariÃ©(e)',
      'divorce': 'DivorcÃ©(e)',
      'veuf': 'Veuf/Veuve',
      '': 'Non renseignÃ©'
    };
    return situations[value] || value;
  };

  return (
    <div className="form-container">
      <form className="person-form" onSubmit={handleSubmit}>
        <div className="form-header">
          <h2>ğŸ“ Inscription - {selectedChoice === 'travail' ? 'Travailleur' : 'Non travailleur'}</h2>
          <p>ComplÃ©tez vos informations pour rejoindre le rÃ©seau</p>
         {/* <div className="network-notice">
            ğŸŒ Votre profil sera visible par tous les membres
          </div>
          
           {Message d'information sur les doublons }
          <div className="duplicate-warning">
            âš ï¸ Le systÃ¨me vÃ©rifie les doublons complets (PrÃ©nom + Nom + MÃ©tier + TÃ©lÃ©phone)
          </div>*/}
        </div>
        
        <div className="selected-choice-banner">
          <div className={`choice-indicator ${selectedChoice}`}>
            {selectedChoice === 'travail' ? 'ğŸ’¼ Vous Ãªtes travailleur' : 'ğŸ‘¤ Vous Ãªtes en quÃªte d\'emploi'}
          </div>
        </div>
        
        <div className="form-sections">
          <section className="form-section">
            <h3>ğŸ‘¤ Informations Personnelles</h3>
            
            <div className="form-row">
              <div className="form-group">
                <label>PrÃ©nom *</label>
                <input
                  type="text"
                  name="prenom"
                  value={formData.prenom}
                  onChange={handleChange}
                  required
                  placeholder="Votre prÃ©nom"
                  className={errors.profession || errors.formation ? 'input-warning' : ''}
                />
              </div>

              <div className="form-group">
                <label>Nom *</label>
                <input
                  type="text"
                  name="nom"
                  value={formData.nom}
                  onChange={handleChange}
                  required
                  placeholder="Votre nom"
                  className={errors.profession || errors.formation ? 'input-warning' : ''}
                />
              </div>
            </div>

            <div className="form-row">
              <div className="form-group">
                <label>Genre</label>
                <select
                  name="genre"
                  value={formData.genre}
                  onChange={handleChange}
                >
                  <option value="">Choisir...</option>
                  <option value="masculin">ğŸ‘¨ Masculin</option>
                  <option value="feminin">ğŸ‘© FÃ©minin</option>
                </select>
              </div>

              <div className="form-group">
                <label>Situation matrimoniale</label>
                <select
                  name="situationMatrimoniale"
                  value={formData.situationMatrimoniale}
                  onChange={handleChange}
                >
                  <option value="">Choisir...</option>
                  <option value="celibataire">CÃ©libataire</option>
                  <option value="marie">MariÃ©(e)</option>
                  <option value="divorce">DivorcÃ©(e)</option>
                  <option value="veuf">Veuf/Veuve</option>
                </select>
              </div>
            </div>

            <div className="form-group">
              <label>TÃ©lÃ©phone *</label>
              <input
                type="tel"
                name="telephone"
                value={formData.telephone}
                onChange={handleChange}
                required
                placeholder="Ex: +223 77 13 45 67"
                className={errors.profession || errors.formation ? 'input-warning' : ''}
              />
              {(errors.profession || errors.formation) && (
                <div className="field-warning">
                  Le tÃ©lÃ©phone fait partie des critÃ¨res de vÃ©rification des doublons
                </div>
              )}
            </div>
          </section>

          <section className="form-section">
            <h3>ğŸ“ Localisation</h3>
            
            <div className="form-group">
              <label>Quartier *</label>
              <input
                type="text"
                name="quartier"
                value={formData.quartier}
                onChange={handleChange}
                required
                placeholder="Ex: Titibougou, Kalanba-coura..."
              />
            </div>

            <div className="form-row">
              <div className="form-group">
                <label>RÃ©gion *</label>
                <input
                  type="text"
                  name="region"
                  value={formData.region}
                  onChange={handleChange}
                  required
                  placeholder="Ex: Bamako, Bougouni..."
                />
              </div>

              <div className="form-group">
                <label>Pays *</label>
                <select
                  name="pays"
                  value={formData.pays}
                  onChange={handleChange}
                >
                  <option value="Mali">Mali</option>
                  <option value="SÃ©nÃ©gal">SÃ©nÃ©gal</option>
                  <option value="CÃ´te d'Ivoire">CÃ´te d'Ivoire</option>
                  <option value="GuinÃ©e">GuinÃ©e</option>
                  <option value="France">France</option>
                  <option value="Autre">Autre</option>
                </select>
              </div>
            </div>
          </section>

          <section className="form-section">
            <h3>ğŸ’¼ Situation Professionnelle</h3>
            
            <div className="selected-option-display">
              <div className={`option-display ${selectedChoice}`}>
                <div className="option-display-icon">
                  {selectedChoice === 'travail' ? 'ğŸ’¼' : 'ğŸ‘¤'}
                </div>
                <div className="option-display-text">
                  <strong>
                    {selectedChoice === 'travail' ? 'Travailleur' : 'En quÃªte d\'emploi'}
                  </strong>
                  <small>
                    {selectedChoice === 'travail' 
                      ? 'Vous Ãªtes actuellement en poste professionnel' 
                      : 'Vous Ãªtes Ã  la recherche d\'un emploi'
                    }
                  </small>
                </div>
              </div>
            </div>

            {selectedChoice === 'travail' && (
              <div className="option-fields">
                <div className="form-group">
                  <label>Profession *</label>
                  <input
                    type="text"
                    name="profession"
                    value={formData.profession}
                    onChange={handleChange}
                    required
                    placeholder="Ex: Informaticien, MÃ©decin, Enseignant..."
                    className={errors.profession ? 'input-error' : ''}
                  />
                  {errors.profession && (
                    <div className="error-message duplicate-error">
                      {errors.profession.split('\n').map((line, index) => (
                        <div key={index}>{line}</div>
                      ))}
                    </div>
                  )}
                  <div className="field-info">
                    ğŸ’¡ VÃ©rification: PrÃ©nom + Nom + Profession + Entreprise + TÃ©lÃ©phone
                  </div>
                </div>

                <div className="form-group">
                  <label>Entreprise *</label>
                  <input
                    type="text"
                    name="entreprise"
                    value={formData.entreprise}
                    onChange={handleChange}
                    required
                    placeholder="Nom de votre entreprise"
                    className={errors.profession ? 'input-warning' : ''}
                  />
                  {errors.profession && (
                    <div className="field-warning">
                      L'entreprise fait partie des critÃ¨res de vÃ©rification
                    </div>
                  )}
                </div>
              </div>
            )}

            {selectedChoice === 'non-travail' && (
              <div className="option-fields">
                <div className="form-group">
                  <label>MÃ©tier de formation *</label>
                  <input
                    type="text"
                    name="formation"
                    value={formData.formation}
                    onChange={handleChange}
                    required
                    placeholder="Ex: Informatique, MÃ©canique, Commerce..."
                    className={errors.formation ? 'input-error' : ''}
                  />
                  {errors.formation && (
                    <div className="error-message duplicate-error">
                      {errors.formation.split('\n').map((line, index) => (
                        <div key={index}>{line}</div>
                      ))}
                    </div>
                  )}
                  <div className="field-info">
                    ğŸ’¡ VÃ©rification: PrÃ©nom + Nom + MÃ©tier de formation + TÃ©lÃ©phone
                  </div>
                </div>
              </div>
            )}
          </section>

          <section className="form-section">
            <h3>ğŸ“š Information Daara</h3>
            
            <div className="form-group">
              <label>Nom du Daara *</label>
              <input
                type="text"
                name="daara"
                value={formData.daara}
                onChange={handleChange}
                required
                placeholder="Nom de votre Daara"
              />
            </div>
          </section>
        </div>

        {/* Affichage des erreurs gÃ©nÃ©rales */}
        {(errors.profession || errors.formation) && (
          <div className="global-error">
            <h4>ğŸš« Inscription impossible - Doublon dÃ©tectÃ©</h4>
            <p>Cette combinaison existe dÃ©jÃ  dans la base de donnÃ©es.</p>
            <p>Veuillez modifier les informations en surbrillance.</p>
          </div>
        )}

        <div className="form-actions">
          <button 
            type="submit" 
            className="submit-btn"
            disabled={isSubmitting || Object.keys(errors).length > 0}
          >
            {isSubmitting ? 'â³ VÃ©rification...' : 'âœ… VÃ©rifier et s\'inscrire'}
          </button>
        </div>
      </form>

      {/* Modal de confirmation */}
      {showConfirmation && (
        <div className="modal-overlay">
          <div className="confirmation-modal">
            <div className="modal-header">
              <h3>ğŸ” VÃ©rifiez vos informations</h3>
              <p>Confirmez que toutes les informations sont correctes avant de vous inscrire</p>
              <div className="public-notice">
                ğŸŒ Ces informations seront publiques dans le rÃ©seau
              </div>
              
              {/* Message de confirmation de non-doublon */}
              <div className="duplicate-check-success">
                âœ… Aucun doublon dÃ©tectÃ© - Inscription autorisÃ©e
              </div>
            </div>

            <div className="confirmation-content">
              {/* Le contenu reste le mÃªme */}
              {/* ... */}
            </div>

