import React, { useState, useMemo } from 'react';
import SearchFilter from '../components/SearchFilter';
import PersonCard from '../components/PersonCard';
import '../styles/Search.css';

const Search = ({ persons }) => {
  const [filters, setFilters] = useState({
    searchTerm: '',
    situation: '',
    profession: '',
    region: '',
    quartier: '',
    daara: ''
  });

  const filteredPersons = useMemo(() => {
    return persons.filter(person => {
      const matchesSearch = filters.searchTerm === '' || 
        Object.values(person).some(value => 
          String(value).toLowerCase().includes(filters.searchTerm.toLowerCase())
        );

      const matchesSituation = filters.situation === '' || 
                               filters.situation === 'tous' ||
                               (filters.situation === 'travail' && person.travail === true) ||
                               (filters.situation === 'formation' && person.travail === false);

      const matchesProfession = filters.profession === '' || 
        (person.profession && person.profession.toLowerCase().includes(filters.profession.toLowerCase()));

      const matchesRegion = filters.region === '' || 
        person.region.toLowerCase().includes(filters.region.toLowerCase());

      const matchesQuartier = filters.quartier === '' || 
        person.quartier.toLowerCase().includes(filters.quartier.toLowerCase());

      const matchesDaara = filters.daara === '' || 
        person.daara.toLowerCase().includes(filters.daara.toLowerCase());

      return matchesSearch && matchesSituation && matchesProfession && matchesRegion && matchesQuartier && matchesDaara;
    });
  }, [persons, filters]);

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters(prev => ({
      ...prev,
      [name]: value
    }));
  };

  return (
    <div className="search-page">
      <div className="container">
        <div className="search-header">
          <h1>üîç Rechercher des Membres</h1>
          <p>Trouvez des personnes par profession, r√©gion, quartier ou daara</p>
        </div>

        <div className="search-content">
          <div className="filters-sidebar">
            <SearchFilter filters={filters} onFilterChange={handleFilterChange} />
          </div>

          <div className="results-main">
            <div className="results-info">
              <h3>{filteredPersons.length} membre(s) trouv√©(s)</h3>
              {filters.situation && (
                <p>Filtre: {filters.situation === 'travail' ? 'Travailleurs' : 
                           filters.situation === 'formation' ? 'En formation' : 'Tous'}</p>
              )}
            </div>

            <div className="persons-grid">
              {filteredPersons.map(person => (
                <PersonCard key={person.id} person={person} />
              ))}
            </div>

            {filteredPersons.length === 0 && (
              <div className="no-results">
                <div className="no-results-icon">üîç</div>
                <h3>Aucun membre ne correspond √† votre recherche</h3>
                <p>Essayez de modifier vos crit√®res de recherche</p>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Search;
