import React from 'react';
import '../styles/SearchFilter.css';

const SearchFilter = ({ filters, onFilterChange }) => {
  const handleOptionSelect = (situation) => {
    onFilterChange({ target: { name: 'situation', value: situation } });
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    onFilterChange({ target: { name, value } });
  };

  return (
    <div className="search-filters">
      <div className="filters-header">
        <h3>üîç Filtres de Recherche</h3>
      </div>

      <div className="filter-group">
        <label>Recherche globale</label>
        <input
          type="text"
          name="searchTerm"
          value={filters.searchTerm}
          onChange={handleInputChange}
          placeholder="Nom, pr√©nom, t√©l√©phone..."
          className="filter-input"
        />
      </div>

   

      <div className="filter-group">
        <label>R√©gion</label>
        <input
          type="text"
          name="region"
          value={filters.region}
          onChange={handleInputChange}
          placeholder="Ex: Dakar, Thi√®s..."
          className="filter-input"
        />
      </div>

      <div className="filter-group">
        <label>Quartier</label>
        <input
          type="text"
          name="quartier"
          value={filters.quartier}
          onChange={handleInputChange}
          placeholder="Ex: HLM, Grand Yoff..."
          className="filter-input"
        />
      </div>

      <div className="filter-group">
        <label>Daara</label>
        <input
          type="text"
          name="daara"
          value={filters.daara}
          onChange={handleInputChange}
          placeholder="Nom du Daara"
          className="filter-input"
        />
      </div>
    </div>
  );
};

export default SearchFilter;
