import React from 'react';
import '../styles/PersonCard.css';

const PersonCard = ({ person, showStatus = false }) => {
  const isTravailleur = person.travail === true;
  
  // Fonction pour g√©n√©rer une couleur bas√©e sur le statut
  const getProfileColor = () => {
    return isTravailleur 
      ? { background: 'linear-gradient(135deg, #27ae60, #2ecc71)', color: '#fff' }
      : { background: 'linear-gradient(135deg, #e74c3c, #e67e22)', color: '#fff' };
  };

  // Fonction pour g√©n√©rer les initiales
  const getInitials = () => {
    const prenomInitial = person.prenom ? person.prenom.charAt(0).toUpperCase() : '';
    const nomInitial = person.nom ? person.nom.charAt(0).toUpperCase() : '';
    return `${prenomInitial}${nomInitial}`;
  };

  return (
    <div className={`person-card ${isTravailleur ? 'travail' : 'non-travail'}`}>
      {/* Badge de statut */}
      {showStatus && (
        <div className={`person-status ${isTravailleur ? 'status-travail' : 'status-non-travail'}`}>
          {isTravailleur ? 'üíº Travaille' : 'üë§ Recherche'}
        </div>
      )}
      
      <div className="person-photo">
        {person.photo ? (
          <img src={person.photo} alt={`${person.prenom} ${person.nom}`} />
        ) : (
          <div 
            className="photo-placeholder"
            style={getProfileColor()}
          >
            {getInitials()}
          </div>
        )}
      </div>
      
      <div className="person-info">
        <h3>{person.prenom} {person.nom}</h3>
        <p className="person-phone">üìû {person.telephone}</p>
        <p className="person-location">üìç {person.quartier}, {person.region}</p>
        
        {isTravailleur ? (
          <>
            <p className="person-profession">üíº {person.profession}</p>
            <p className="person-entreprise">üè¢ {person.entreprise}</p>
          </>
        ) : (
          <>
            <p className="person-formation">üéì {person.formation}</p>
            {person.profession && <p className="person-niveau">üìö {person.profession}</p>}
          </>
        )}
        
        <p className="person-daara">üìö {person.daara}</p>
      </div>
    </div>
  );
};

export default PersonCard;
