import React, { useState, useEffect } from 'react';
import { supabase } from './supabase';
import './styles/App.css';
import Header from './components/Header';
import Home from './pages/Home';
import Register from './pages/Register';
import Search from './pages/Search';

function App() {
  const [currentPage, setCurrentPage] = useState('home');
  const [persons, setPersons] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Charger les donn√©es depuis Supabase
  useEffect(() => {
    fetchPersons();
    
    // S'abonner aux changements en temps r√©el
    const subscription = supabase
      .channel('persons-changes')
      .on('postgres_changes', 
        { event: 'INSERT', schema: 'public', table: 'persons' }, 
        (payload) => {
          console.log('Nouvelle personne ins√©r√©e:', payload.new);
          setPersons(prev => [payload.new, ...prev]);
        }
      )
      .subscribe();

    return () => {
      subscription.unsubscribe();
    };
  }, []);

  const fetchPersons = async () => {
    try {
      setLoading(true);
      setError(null);
      
      const { data, error } = await supabase
        .from('persons')
        .select('*')
        .order('created_at', { ascending: false });

      if (error) throw error;
      
      console.log('Donn√©es charg√©es:', data);
      setPersons(data || []);
    } catch (error) {
      console.error('Erreur lors du chargement:', error);
      setError('Erreur de connexion √† la base de donn√©es');
      
      // Fallback: charger depuis localStorage
      const savedPersons = localStorage.getItem('begueBaaraPersons');
      if (savedPersons) {
        const localData = JSON.parse(savedPersons);
        console.log('Donn√©es locales charg√©es:', localData);
        setPersons(localData);
      }
    } finally {
      setLoading(false);
    }
  };

  const addPerson = async (personData) => {
    try {
      setError(null);
      console.log('Tentative d\'ajout:', personData);
      
      const { data, error } = await supabase
        .from('persons')
        .insert([{
          nom: personData.nom,
          prenom: personData.prenom,
          telephone: personData.telephone,
          quartier: personData.quartier,
          region: personData.region,
          pays: personData.pays,
          situation_matrimoniale: personData.situationMatrimoniale,
          profession: personData.profession,
          entreprise: personData.entreprise,
          formation: personData.formation,
          daara: personData.daara,
          travail: personData.travail
        }])
        .select();

      if (error) {
        console.error('Erreur Supabase:', error);
        throw error;
      }
      
      console.log('Personne ajout√©e avec succ√®s:', data[0]);
      
      // Mise √† jour imm√©diate de l'√©tat local
      const newPerson = data[0];
      setPersons(prev => [newPerson, ...prev]);
      
      // Sauvegarde locale en backup
      const updatedPersons = [newPerson, ...persons];
      localStorage.setItem('begueBaaraPersons', JSON.stringify(updatedPersons));
      
      return { success: true, data: newPerson };
      
    } catch (error) {
      console.error('Erreur compl√®te:', error);
      setError('Erreur lors de l\'inscription: ' + error.message);
      
      // Fallback: sauvegarde locale
      const newPerson = {
        id: Date.now().toString(),
        ...personData,
        created_at: new Date().toISOString()
      };
      const updatedPersons = [newPerson, ...persons];
      setPersons(updatedPersons);
      localStorage.setItem('begueBaaraPersons', JSON.stringify(updatedPersons));
      
      return { success: true, data: newPerson };
    }
  };

  const deletePerson = async (personId) => {
    try {
      const { error } = await supabase
        .from('persons')
        .delete()
        .eq('id', personId);

      if (error) throw error;
      
      // Mise √† jour imm√©diate de l'√©tat local
      setPersons(prev => prev.filter(p => p.id !== personId));
      
      return { success: true };
    } catch (error) {
      console.error('Erreur lors de la suppression:', error);
      setError('Erreur lors de la suppression: ' + error.message);
      return { success: false, error: error.message };
    }
  };

  const renderPage = () => {
    if (loading) {
      return (
        <div className="loading-container">
          <div className="loading-spinner"></div>
          <p>Chargement de l'application B√©gu√© Baara...</p>
        </div>
      );
    }

    if (error && persons.length === 0) {
      return (
        <div className="error-container">
          <div className="error-icon">‚ö†Ô∏è</div>
          <h3>Erreur de connexion</h3>
          <p>{error}</p>
          <p>Utilisation du mode hors ligne avec les donn√©es locales.</p>
          <button onClick={fetchPersons} className="btn-retry">
            üîÑ R√©essayer la connexion
          </button>
        </div>
      );
    }

    switch(currentPage) {
      case 'home':
        return <Home persons={persons} />;
      case 'register':
        return <Register onAddPerson={addPerson} />;
      case 'search':
        return <Search persons={persons} onDeletePerson={deletePerson} />;
      default:
        return <Home persons={persons} />;
    }
  };

  return (
    <div className="App">
      <Header currentPage={currentPage} setCurrentPage={setCurrentPage} />
      <main className="main-content">
        {renderPage()}
      </main>
      
      <footer className="footer">
        <div className="container">
          <div className="footer-content">
            <div className="footer-section">
              <h3>B√©gu√© Baara</h3>
              <p>Le r√©seau social de la communaut√© Allah Don</p>
              <p>Connectant plus de {persons.length} membres</p>
              {error && (
                <div className="connection-status offline">
                  üî¥ Mode hors ligne
                </div>
              )}
              {!error && (
                <div className="connection-status online">
                  üü¢ Connect√© - {persons.length} membres
                </div>
              )}
            </div>
            <div className="footer-section">
              <h4>Navigation</h4>
              <button onClick={() => setCurrentPage('home')}>Accueil</button>
              <button onClick={() => setCurrentPage('register')}>S'inscrire</button>
              <button onClick={() => setCurrentPage('search')}>Rechercher</button>
            </div>
            <div className="footer-section">
              <h4>Contact</h4>
              <p>üìß contact@beguebaara.org</p>
              <p>üìû +223 76 32 64 28 / 75 23 48 44 / 92 87 73 35</p>
              <p>üìç Bougouni, Mali</p>
            </div>
          </div>
          <div className="footer-bottom">
            <p>&copy; 2025 B√©gu√© Baara. Tous droits r√©serv√©s.</p>
          </div>
        </div>
      </footer>
    </div>
  );
}

export default App;
