import React, { useState, useEffect } from 'react';
import { supabase } from './supabase';
import './styles/App.css';
import Header from './components/Header';
import Home from './pages/Home';
import Register from './pages/Register';
import Search from './pages/Search';

function App() {
  const [currentPage, setCurrentPage] = useState('home');
  const [persons, setPersons] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Charger les donn√©es depuis Supabase
  useEffect(() => {
    fetchPersons();
  }, []);

  const fetchPersons = async () => {
    try {
      setLoading(true);
      setError(null);
      
      console.log('üîç Chargement des donn√©es depuis Supabase...');
      
      const { data, error } = await supabase
        .from('persons')
        .select('*')
        .order('created_at', { ascending: false });

      if (error) {
        console.error('‚ùå Erreur Supabase:', error);
        throw error;
      }
      
      console.log('‚úÖ Donn√©es charg√©es:', data?.length || 0, 'personnes');
      setPersons(data || []);
      
    } catch (error) {
      console.error('‚ùå Erreur lors du chargement:', error);
      setError('Erreur de connexion √† la base de donn√©es');
      
      // Dans fetchPersons :
const mappedData = (data || []).map(mapPersonFromSupabase);
setPersons(mappedData);

      // Fallback: charger depuis localStorage
      const savedPersons = localStorage.getItem('begueBaaraPersons');
      if (savedPersons) {
        try {
          const localData = JSON.parse(savedPersons);
          console.log('üìÅ Donn√©es locales charg√©es:', localData.length, 'personnes');
          setPersons(localData);
        } catch (e) {
          console.error('‚ùå Erreur donn√©es locales:', e);
        }
      }
    } finally {
      setLoading(false);
    }
  };

  const mapPersonFromSupabase = (person) => ({
  id: person.id,
  nom: person.nom,
  prenom: person.prenom,
  telephone: person.telephone,
  quartier: person.quartier,
  region: person.region,
  pays: person.pays,
  genre: person.genre,
  situationMatrimoniale: person.situation_matrimoniale, // mapping
  profession: person.profession,
  entreprise: person.entreprise,
  formation: person.formation,
  daara: person.daara,
  travail: person.travail,
  created_at: person.created_at
});


  const addPerson = async (personData) => {
    try {
      setError(null);
      console.log('‚ûï Tentative d\'ajout:', personData);
      
      // Pr√©parer les donn√©es pour Supabase
      const supabaseData = {
        nom: personData.nom,
        prenom: personData.prenom,
        telephone: personData.telephone,
        quartier: personData.quartier,
        region: personData.region,
        pays: personData.pays,
        genre: personData.genre || null,
        situation_matrimoniale: personData.situationMatrimoniale || null,
        profession: personData.profession || null,
        entreprise: personData.entreprise || null,
        formation: personData.formation || null,
        daara: personData.daara,
        travail: personData.travail
      };

      console.log('üì§ Envoi √† Supabase:', supabaseData);

      const { data, error } = await supabase
        .from('persons')
        .insert([supabaseData])
        .select();

      if (error) {
        console.error('‚ùå Erreur Supabase:', error);
        throw error;
      }
      
      if (!data || data.length === 0) {
        throw new Error('Aucune donn√©e retourn√©e par Supabase');
      }
      
      const newPerson = data[0];
      console.log('‚úÖ Personne ajout√©e avec succ√®s:', newPerson);
      
      // Mise √† jour IMM√âDIATE de l'√©tat local
      setPersons(prev => {
        const updated = [newPerson, ...prev];
        console.log('üîÑ Liste mise √† jour:', updated.length, 'personnes');
        return updated;
      });
      
      // Sauvegarde locale en backup
      try {
        const updatedPersons = [newPerson, ...persons];
        localStorage.setItem('begueBaaraPersons', JSON.stringify(updatedPersons));
        console.log('üíæ Sauvegarde locale r√©ussie');
      } catch (e) {
        console.error('‚ùå Erreur sauvegarde locale:', e);
      }
      
      return { success: true, data: newPerson };
      
    } catch (error) {
      console.error('‚ùå Erreur compl√®te lors de l\'ajout:', error);
      
      // Fallback: sauvegarde locale
      console.log('üîÑ Utilisation du mode fallback local...');
      const newPerson = {
        id: Date.now().toString(),
        ...personData,
        created_at: new Date().toISOString()
      };
      
      const updatedPersons = [newPerson, ...persons];
      setPersons(updatedPersons);
      
      try {
        localStorage.setItem('begueBaaraPersons', JSON.stringify(updatedPersons));
        console.log('‚úÖ Fallback local r√©ussi:', newPerson);
      } catch (e) {
        console.error('‚ùå Erreur fallback local:', e);
      }
      
      return { success: true, data: newPerson };
    }
  };

  const deletePerson = async (personId) => {
    try {
      const { error } = await supabase
        .from('persons')
        .delete()
        .eq('id', personId);

      if (error) throw error;
      
      // Mise √† jour imm√©diate de l'√©tat local
      setPersons(prev => prev.filter(p => p.id !== personId));
      
      return { success: true };
    } catch (error) {
      console.error('Erreur lors de la suppression:', error);
      return { success: false, error: error.message };
    }
  };

  const renderPage = () => {
    if (loading) {
      return (
        <div className="loading-container">
          <div className="loading-spinner"></div>
          <p>Chargement de l'application B√©gu√© Baara...</p>
        </div>
      );
    }

    if (error && persons.length === 0) {
      return (
        <div className="error-container">
          <div className="error-icon">‚ö†Ô∏è</div>
          <h3>Erreur de connexion</h3>
          <p>{error}</p>
          <p>Utilisation du mode hors ligne avec les donn√©es locales.</p>
          <button onClick={fetchPersons} className="btn-retry">
            üîÑ R√©essayer la connexion
          </button>
        </div>
      );
    }

    switch(currentPage) {
      case 'home':
        return <Home persons={persons} />;
      case 'register':
        return <Register onAddPerson={addPerson} persons={persons} />;
      case 'search':
        return <Search persons={persons} onDeletePerson={deletePerson} />;
      default:
        return <Home persons={persons} />;
    }
  };

  return (
    <div className="App">
      <Header currentPage={currentPage} setCurrentPage={setCurrentPage} />
      <main className="main-content">
        {renderPage()}
      </main>
      
      {/* Debug info - √Ä retirer en production 
      <div style={{
        position: 'fixed',
        bottom: '10px',
        left: '10px',
        background: 'rgba(0,0,0,0.8)',
        color: 'white',
        padding: '10px',
        borderRadius: '5px',
        fontSize: '12px',
        zIndex: 1000
      }}>
        <div>üîç Debug: {persons.length} personnes</div>
        <div>üì± Page: {currentPage}</div>
      </div> */}
      
      <footer className="footer">
        <div className="container">
          <div className="footer-content">
            <div className="footer-section">
              <h3>B√©gu√© Baara</h3>
              <p>Le r√©seau social de la communaut√© Allah Don</p>
              <p>Connectant plus de {persons.length} membres</p>
            </div>
            <div className="footer-section">
              <h4>Navigation</h4>
              <button onClick={() => setCurrentPage('home')}>Accueil</button>
              <button onClick={() => setCurrentPage('register')}>S'inscrire</button>
              <button onClick={() => setCurrentPage('search')}>Rechercher</button>
            </div>
            <div className="footer-section">
              <h4>Contact</h4>
              <p>üìß contact@beguebaara.org</p>
              <p>üìû +223 76 32 64 28 / 75 23 48 44 / 92 87 73 35</p>
              <p>üìç Bougouni, Mali</p>
            </div>
          </div>
          <div className="footer-bottom">
            <p>&copy; 2025 B√©gu√© Baara. Tous droits r√©serv√©s.</p>
          </div>
        </div>
      </footer>
    </div>
  );
}

export default App;
