import React, { useState } from 'react';
import PersonForm from '../components/PersonForm';
import '../styles/Register.css';

const Register = ({ onAddPerson }) => {
  const [step, setStep] = useState('choice'); // 'choice' ou 'form'

  const handleChoiceSelect = (choice) => {
    setStep('form');
  };

  const handleBackToChoice = () => {
    setStep('choice');
  };

  if (step === 'choice') {
    return (
      <div className="register-page">
        <div className="container">
          <div className="choice-section">
            <div className="choice-header">
              <h1>üîÑ Choisissez votre situation</h1>
              <p>S√©lectionnez votre situation professionnelle pour commencer l'inscription</p>
            </div>
            
            <div className="choice-buttons">
              <button 
                className="choice-button travail"
                onClick={() => handleChoiceSelect('travail')}
              >
                <div className="choice-icon">üíº</div>
                <div className="choice-text">
                  <h3>Je travaille</h3>
                  <p>Actuellement en poste professionnel</p>
                </div>
                <div className="choice-arrow">‚Üí</div>
              </button>

              <button 
                className="choice-button formation"
                onClick={() => handleChoiceSelect('formation')}
              >
                <div className="choice-icon">üéì</div>
                <div className="choice-text">
                  <h3>Je ne travaille pas</h3>
                  <p>En formation, √©tudes ou autre situation</p>
                </div>
                <div className="choice-arrow">‚Üí</div>
              </button>
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="register-page">
      <div className="container">
        <button className="back-button" onClick={handleBackToChoice}>
          ‚Üê Retour au choix
        </button>
        <PersonForm onAddPerson={onAddPerson} />
      </div>
    </div>
  );
};

export default Register;
