import React from 'react';
import '../styles/SearchFilter.css';

const SearchFilter = ({ filters, onFilterChange, selectedSituation }) => {
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    onFilterChange({ target: { name, value } });
  };

  return (
    <div className="search-filters">
      <div className="filters-header">
        <h3>üîç Filtres de Recherche</h3>
        {selectedSituation && (
          <div className="selected-situation">
            Recherche : {selectedSituation === 'travail' ? 'Travailleurs' : 
                        selectedSituation === 'non-travail' ? 'Non travailleurs' : 'Tous les membres'}
          </div>
        )}
      </div>

      <div className="filter-group">
        <label>Recherche globale</label>
        <input
          type="text"
          name="searchTerm"
          value={filters.searchTerm}
          onChange={handleInputChange}
          placeholder="Nom, pr√©nom, t√©l√©phone..."
          className="filter-input"
        />
      </div>

      {/* Filtres d√©mographiques */}
      <div className="filter-row">
        <div className="filter-group">
          <label>üë®‚Äçüë©‚Äçüëß‚Äçüë¶ Genre</label>
          <select
            name="genre"
            value={filters.genre}
            onChange={handleInputChange}
            className="filter-select"
          >
            <option value="">Tous les genres</option>
            <option value="masculin">üë® Masculin</option>
            <option value="feminin">üë© F√©minin</option>
          </select>
        </div>

        <div className="filter-group">
          <label>üíç Situation matrimoniale</label>
          <select
            name="situationMatrimoniale"
            value={filters.situationMatrimoniale}
            onChange={handleInputChange}
            className="filter-select"
          >
            <option value="">Toutes les situations</option>
            <option value="celibataire">C√©libataire</option>
            <option value="marie">Mari√©(e)</option>
            <option value="divorce">Divorc√©(e)</option>
            <option value="veuf">Veuf/Veuve</option>
          </select>
        </div>
      </div>

      {/* Filtres sp√©cifiques aux travailleurs */}
      {selectedSituation === 'travail' && (
        <>
          <div className="filter-group">
            <label>üîç Recherche par profession</label>
            <input
              type="text"
              name="profession"
              value={filters.profession}
              onChange={handleInputChange}
              placeholder="Ex: Informaticien, M√©decin, Enseignant..."
              className="filter-input"
            />
          </div>

          <div className="filter-group">
            <label>üè¢ Recherche par entreprise</label>
            <input
              type="text"
              name="entreprise"
              value={filters.entreprise}
              onChange={handleInputChange}
              placeholder="Nom de l'entreprise"
              className="filter-input"
            />
          </div>
        </>
      )}

      {/* Filtres sp√©cifiques aux non-travailleurs */}
      {selectedSituation === 'non-travail' && (
        <div className="filter-group">
          <label>üéì Recherche par domaine</label>
          <input
            type="text"
            name="formation"
            value={filters.formation}
            onChange={handleInputChange}
            placeholder="Ex: M√©canique, Informatique, Commerce..."
            className="filter-input"
          />
        </div>
      )}

      {/* Filtre profession pour "Tous les membres" ou mixte */}
      {(selectedSituation === 'tous' || !selectedSituation) && (
        <div className="filter-group">
          <label>üíº Profession (tous types)</label>
          <input
            type="text"
            name="profession"
            value={filters.profession}
            onChange={handleInputChange}
            placeholder="Ex: Informaticien, M√©decin, √âtudiant..."
            className="filter-input"
          />
        </div>
      )}

      <div className="filter-group">
        <label>üìç R√©gion</label>
        <input
          type="text"
          name="region"
          value={filters.region}
          onChange={handleInputChange}
          placeholder="Ex: Bamako, Bougouni..."
          className="filter-input"
        />
      </div>

      <div className="filter-group">
        <label>üèòÔ∏è Quartier</label>
        <input
          type="text"
          name="quartier"
          value={filters.quartier}
          onChange={handleInputChange}
          placeholder="Ex: Titibougou, Kalanba-coura..."
          className="filter-input"
        />
      </div>

      <div className="filter-group">
        <label>üìö Daara</label>
        <input
          type="text"
          name="daara"
          value={filters.daara}
          onChange={handleInputChange}
          placeholder="Nom du Daara"
          className="filter-input"
        />
      </div>
    </div>
  );
};

export default SearchFilter;
